mapper.select:
SELECT USER_NO
FROM aws.USER_INFO
WHERE EMAIL=#{email}

mapper.update:
UPDATE aws.USER_INFO
SET USER_AUTH='Y'
WHERE USER_NO=#{user_no}

mapper.select:
 
	SELECT USER_NO,USER_ID, EMAIL,PASSWORD,USER_AUTH 
	FROM aws.USER_INFO 
	WHERE EMAIL=#{email} and PASSWORD=#{password}

mapper.update:
update aws.USER_INFO
set PASSWORD=#{password}
where EMAIL=#{email}

mapper.select:
select PASSWORD
from aws.USER_INFO
where EMAIL =#{email}

mapper.update:
update aws.USER_INFO
set USER_ID=#{user_id}, AGE=#{age}, JOB=#{job}, INCOME=#{income}, GENDER=#{gender}
where USER_NO=#{user_no}
